private gestureEndTime: number = 0;

// Call when gesture ends
markGestureEnd(): void {
  this.gestureEndTime = performance.now();
}

// Detect rebound zoom-out at max zoom
isReboundZoomOut(newZoom: number): boolean {
  const currentZoom = this.state.zoom;
  const isAtMax = currentZoom >= this.config.maxZoom - 0.01;
  const isZoomingOut = newZoom < currentZoom;
  const timeSinceGestureEnd = performance.now() - this.gestureEndTime;
  const inReboundWindow = timeSinceGestureEnd < 600; // 600ms window

  return isAtMax && isZoomingOut && inReboundWindow;
}

// Keep gesture alive during rebound filtering
signalOngoingActivity(): void {
  // Reset gesture-end timer so settling doesn't trigger
  this.lastActivityTime = performance.now();
}
